/* Users */
CREATE TABLE users (
  id SERIAL PRIMARY KEY,
  username VARCHAR(256) NOT NULL,
  password VARCHAR(256) NOT NULL,
  created_at TIMESTAMP,
  updated_at TIMESTAMP,

  CONSTRAINT unique_username UNIQUE (username)
);
CREATE INDEX index_username ON users (username);


/* Genres */
CREATE TABLE genres (
  id SERIAL PRIMARY KEY,
  name VARCHAR(256) NOT NULL,
  created_at TIMESTAMP,
  updated_at TIMESTAMP,

  CONSTRAINT unique_genre UNIQUE (name)
);
CREATE INDEX index_genre_name ON genres (name);



/* Artists */
CREATE TABLE artists (
  id SERIAL PRIMARY KEY,
  name VARCHAR(256) NOT NULL,
  created_at TIMESTAMP,
  updated_at TIMESTAMP
);
CREATE INDEX index_artist_name ON artists (name);



/* Albums */
CREATE TABLE albums (
  id SERIAL PRIMARY KEY,
  name VARCHAR(256) NOT NULL,
  images TEXT[],
  song_count INT NOT NULL,
  price JSON,
  label_rights VARCHAR(512) NOT NULL,
  title VARCHAR(256) NOT NULL,
  url VARCHAR(256) NOT NULL,
  artist_id INT NOT NULL,
  genre_id INT NOT NULL,
  release_date TIMESTAMP NOT NULL,
  created_at TIMESTAMP,
  updated_at TIMESTAMP,
  
  CONSTRAINT fk_artist
    FOREIGN KEY(artist_id) 
	REFERENCES artists(id),

  CONSTRAINT fk_genre
    FOREIGN KEY(genre_id) 
	REFERENCES genres(id)
);
CREATE INDEX index_albums_name ON albums (name);
CREATE INDEX index_albums_artist ON albums (artist_id);
CREATE INDEX index_albums_genre ON albums (genre_id);

